import React, { Component } from 'react';
import Particles from 'react-particles-js';
import Particlesjsconfig from './particlesjs-config.json';
import Navigation from './components/Navigation/Navigation.js';
import Logo from './components/Logo/Logo.js';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm.js';
import Rank from './components/Rank/Rank.js';
import './App.css';

class App extends Component { 
  constructor() { //We create state to allow us to transport data between child and parent.
    super();
    this.state = {
      input: '',
    }
  }

  onInputChange = (event) => { //This allows the function to listen for an event.
    console.log(event.target.value); //We use this to get the value of the event.
  }

  onSubmit = () => {
    console.log('click');
  }

  render() {
    return (
      <div className="App">
        <Particles className='particles' params={Particlesjsconfig} />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm onInputChange={this.onInputChange} /> //We pass the state as a prop to the child.
        {/*<FaceRecognition />*/}
        </div>
    );
  }
}

export default App;
