import React, { Component } from 'react';
import './App.css';
import CardList from './CardList';
import SearchBox from './SearchBox';

class App extends Component {
  constructor() {
    super()
    this.state = {
      people: [],
      searchfield: '' 
    }
  }

  componentDidMount() {
    var i;

    for (i=1; i < 84; i++) {
      fetch('https://swapi.co/api/people/' + [i])
      .then(resp=>  resp.json())
      .then(results=>  this.setState({people: [results]}));

    }

  }




  render() {
    const { people } = this.state;
    return (
      <div className='tc'>
        <h1>Star Wars Knowledgebase</h1>
        <SearchBox />

          <CardList people={people} />

      </div>
    );
  }
}

export default App;
