import React, { Component } from 'react';
import './App.css';
import CardList from './CardList';
import SearchBox from './SearchBox';

class App extends Component {
  constructor() {
    super()
    this.state = {
      people: [],
      searchfield: '' 
    }
  }

  componentDidMount() {
    var i;
    for (i=0; i < 84; i++) {
      fetch('https://swapi.co/api/people/' + [i])
      .then(resp=>  resp.json())
      .then(results=>  this.setState({people: this.state.people.concat(results)}))
    }

  }

  onSearchChange = (event) => {
      this.setState({ searchfield: event.target.value })
      const filterPeople = this.state.people.filter(person => {
        return this.state.people.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
      })  
    }




  render() {
    const { people } = this.state;
    return (
      <div className='tc'>
        <h1>Star Wars Knowledgebase</h1>
        <SearchBox searchChange={this.onSearchChange}/>

          <CardList people={filterPeople} />
        }

      </div>
    );
  }
}

export default App;
