import React, { Component } from 'react';
import './App.css';
import CardList from './CardList';
import SearchBox from './SearchBox';
import Scroll from './Scroll';

class App extends Component {
  constructor() {
    super()
    this.state = {
      people: [],
      searchfield: '' 
    }
  }

  componentDidMount() {
    var i;
    for (i=0; i < 84; i++) {
      fetch('https://swapi.co/api/people/' + [i])
      .then(resp=>  resp.json())
      .then(results=>  this.setState({people: this.state.people.concat([results])}))
    }

  }

 onSearchChange = (event) => {
    this.setState({ searchfield: event.target.value })

  }





  render() {
    const { people, searchfield } = this.state;
    const filteredPeople = people.filter(people => {
      return people.name.includes(this.state.searchfield);
    })
    return !people.length ? 
      <h1>Loading...</h1> :
      (
      <div className='tc'>
        <h1>Robofriends</h1>
        <SearchBox searchChange={this.onSearchChange}/>
        <Scroll>
          <CardList robots={filteredPeople} />
        </Scroll>
      </div>
      );  
  }


}

export default App;
